import React, {useState, useEffect} from 'react'
import Tpostatus from './Tpostatus';
import Statusview from './Statusview';

const Home = () => {
    const [userName, setUserName] = useState('');
    const [show, setShow] = useState(false);
    const [userData, setUserData] = useState({});

    const userHomePage = async () => {
        try {
            const res = await fetch('/getdata', {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                },
            });

            const data = await res.json();
            // console.log(data);
            setUserName(data.name);
            setShow(true);
            setUserData(data);

        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        userHomePage();
    }, []);
    return (
        <>
            <div className="home-page">
                <div className="home-div">
                    <p className="pt-5">WELCOME</p>
                    <h1>{userName}</h1>
                    <h2> { show ? 'Happy, to see you back' :  'We Are The MERN Developer' }</h2>
                </div>
            </div>
            <div>
            {userData.work == "TPO" &&( 
                <div className="container emp-profile">

                    <h1>TPO</h1>
                    <Tpostatus />
                    <br/>
                    <h1>Posts</h1>
                    <Statusview />
                    
                 

                </div>
            )}
                
                {userData.work == "student" &&(
                    <div > 
                        <div className="container emp-profile">
                        <h1>Posts</h1>
                       

                        <Statusview />
                      
                        </div>
                    </div>
                )}
                
            </div>

            <div className="contact_info">
     <div className="container-fluid">
         <div className="row">
             <div className="col-lg-10 offset-lg-1">
                 <div className="contact_info_container d-flex flex-lg-row flex-column justify-content-between align-items-between">
                     <div className="contact_info_item d-flex flex-row align-items-center justify-content-start">


                     </div> 



                     <div className="contact_info_item d-flex flex-row align-items-center justify-content-start">

                     </div>


                     <div className="contact_info_item d-flex flex-row align-items-center justify-content-start">

                     </div>


                 </div>
             </div>
         </div>
     </div>
</div>
            
            
        </>
    )
}

export default Home
