import React, {useState, useEffect} from 'react'
import Tpostatus from './Tpostatus';
import Statusview from './Statusview';

const Home = () => {
    const [userName, setUserName] = useState('');
    const [show, setShow] = useState(false);
    const [userData, setUserData] = useState({});

    const userHomePage = async () => {
        try {
            const res = await fetch('/getdata', {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                },
            });

            const data = await res.json();
            // console.log(data);
            setUserName(data.name);
            setShow(true);
            setUserData(data);

        } catch (err) {
            console.log(err);
        }
    }

    useEffect(() => {
        userHomePage();
    }, []);
    return (
        <>
            <div className="home-page">
                <div className="home-div">
                    <p className="pt-5">WELCOME</p>
                    <h1>{userName}</h1>
                    <h2> { show ? 'Happy, to see you back' :  'We Are The MERN Developer' }</h2>
                </div>
            </div>
            <div>
            {userData.work == "TPO" &&( 
                <div className="container emp-profile">

                    <h1>TPO</h1>
                    <Tpostatus />
                    <br/>
                    <h1>Posts</h1>
                    <Statusview />
                    
                 

                </div>
            )}
            &nbsp;
            &nbsp;
            &nbsp;

<div className="container-fluid">
                     <div className="row">
                         <div className="col-4">
                     <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Placements info</h5>
                            <p className="card-text">
                            Here you can see all the information regarding Placements.
                            </p>
                            
                            <button type="button" className="btn btn-primary">Click here</button>
                        </div>
                    </div>
                    </div>

                    <div className="col-4">
                     <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Resources info</h5>
                            <p className="card-text">
                            Here you can find all the resources for your placement preparation.
                            </p>
                            
                            <button type="button" className="btn btn-primary">Click here</button>
                        </div>
                    </div>
                    </div>

                    <div className="col-4">
                    <div className="card">
                            <div className="bg-image hover-overlay ripple" data-mdb-ripple-color="light">
                                <img
                                src="https://mdbootstrap.com/img/new/standard/nature/111.jpg"
                                class="img-fluid"
                                />
                                <a href="#!">
                                <div className="mask" style="background-color: rgba(251, 251, 251, 0.15);"></div>
                                </a>
                            </div>
                            <div className="card-body">
                                <h5 className="card-title">Card title</h5>
                                <p className="card-text">
                                Some quick example text to build on the card title and make up the bulk of the
                                card's content.
                                </p>
                                <a href="#!" className="btn btn-primary">Button</a>
                            </div>
                            </div>
                            </div>
                    </div>
                    </div>



                
                {userData.work == "student" &&(
                    <div > 
                        <div className="container emp-profile">
                        <h1>Posts</h1>
                       

                        <Statusview />
                      
                        </div>
                    </div>
                )}
                
            </div>

          

 


            
            
        </>
    )
}

export default Home
